mapper.select:
SELECT id, propertyName, propertyLabel, propertyValue, propertyUnit FROM t_basicinfo WHERE 1=1
AND propertyName like concat('%', #{propertyName}, '%') OR propertyLabel like concat('%', #{propertyName}, '%')
LIMIT #{offset}, #{pageNumber}

SELECT id, propertyName, propertyLabel, propertyValue, propertyUnit
FROM t_basicinfo
WHERE id=#{id}
