mapper.select:
SELECT id, confParaName, confParaClass, confParaLabel, confParaUnit, moduleLinked FROM t_mconfparam WHERE 1=1
AND moduleLinked=#{moduleLinked}
AND confParaName like concat('%', #{confParaName}, '%') OR confParaClass like concat('%', #{confParaName}, '%') OR confParaLabel like concat('%', #{confParaName}, '%')
LIMIT #{offset}, #{pageNumber}

SELECT id, confParaName, confParaClass, confParaLabel, confParaUnit, moduleLinked
FROM t_mconfparam
WHERE id=#{id}

mapper.update:
UPDATE t_mconfparam
SET
confParaName=#{confParaName},confParaClass=#{confParaClass},confParaLabel=#{confParaLabel},confParaUnit=#{confParaUnit}, moduleLinked=#{moduleLinked}
WHERE id=#{id}
