mapper.select:
SELECT id, moduleName, father, corrClass, corrForm FROM t_mcorrelations WHERE 1=1
AND moduleName=#{moduleName}
AND (father like concat('%', #{father}, '%') OR corrClass like concat('%', #{father}, '%') OR corrForm like concat('%', #{father}, '%'))
LIMIT #{offset}, #{pageNumber}

SELECT id, moduleName, father, corrClass, corrForm
FROM t_mcorrelations
WHERE id=#{id}

SELECT id, moduleName, father, corrClass, corrForm
FROM t_mcorrelations
WHERE father=#{father}

mapper.update:
UPDATE t_mcorrelations
SET
moduleName=#{moduleName}, father=#{father}, corrClass=#{corrClass}, corrForm=#{corrForm}
WHERE id=#{id}
