mapper.select:
SELECT id, ruleLabel, moduleLink, startChar, paraName, comparator, paraValue, logSymbol, linkRule, result
FROM t_rule
WHERE id=#{id}

mapper.update:
UPDATE t_rule
SET
ruleLabel=#{ruleLabel}, moduleLink=#{moduleLink}, startChar=#{startChar}, paraName=#{paraName},
comparator=#{comparator}, paraValue=#{paraValue}, logSymbol=#{logSymbol}, linkRule=#{linkRule},
result=#{result}
WHERE id=#{id}
